mapper.update:
UPDATE pdcontents
SET title=#{title}, content=#{content}, word=#{word}
WHERE pdcontentsno = #{pdcontentsno}

UPDATE pdcontents
SET fname=#{fname}, fupname=#{fupname}, thumb=#{thumb}, fsize=#{fsize} 
WHERE pdcontentsno = #{pdcontentsno}

UPDATE pdreply
SET replycnt = replycnt + 1 
WHERE pdcontentsno=#{pdcontentsno}

UPDATE pdreply
SET replycnt = replycnt - 1 
WHERE pdcontentsno=#{pdcontentsno}

mapper.select:
SELECT COUNT(*) as cnt FROM pdcontents
WHERE productcateno = #{productcateno}
WHERE productcateno =#{productcateno} AND word LIKE '%' || #{word} || '%'

SELECT pdcontentsno, productcateno, title, price, content, recom,cnt,replycnt,rdate,word, fname, fupname, thumb, fsize, r FROM ( SELECT pdcontentsno, productcateno, title, price, content, recom, cnt, replycnt, rdate, word, fname, fupname, thumb, fsize, rownum as r FROM ( SELECT pdcontentsno, productcateno, title, price, content, recom, cnt, replycnt, rdate, word, fname, fupname, thumb, fsize FROM pdcontents
WHERE productcateno=#{productcateno}
WHERE productcateno=#{productcateno} AND word LIKE '%' || #{word} || '%'
ORDER BY pdcontentsno DESC ) ) WHERE r >=#{startNum} AND r <=#{endNum}
